import CPFController from './cpf.controller';
import LoginController from './login.controller';
import UserController from './user.controller';

export { CPFController, LoginController, UserController };

import { Request, Response } from 'express';
import { CPFServices } from '../services';

export default class CPFController {
    public static addCPFToRestrictedList = async (req: Request, res: Response) => {
        try {
            const { cpf } = req.body;

            await CPFServices.addCPF(cpf);

            return res.status(201).json();
        } catch (error) {
            return res.status(500).json(error.message);
        }
    };

    public static findOneCPFOnRestrictedList = async (req: Request, res: Response) => {
        try {
            const params = req.params;

            const CPFresult = await CPFServices.findOneCPF(params.cpf);

            return res.status(200).json(CPFresult);
        } catch (error) {
            return res.status(500).json(error.message);
        }
    };

    public static findAllCPFOnRestrictedList = async (req: Request, res: Response) => {
        try {
            const CPFresult = await CPFServices.findAllCPF();

            return res.status(200).json(CPFresult);
        } catch (error) {
            return res.status(500).json(error.message);
        }
    };

    public static deleteCPFOnRestrictedList = async (req: Request, res: Response) => {
        try {
            const params = req.params;
            await CPFServices.deleteCPF(params.cpf);
            return res.status(204).json();
        } catch (error) {
            return res.status(500).json(error.message);
        }
    };
}

import CPFServices from './CPF/cpf.services';
import UserServices from './user/users.services';

export { CPFServices, UserServices };

import CPFModel from '../../database/models/cpf.model';
import { CPFresponse } from '../../types/types';

export default class CPFService {
    public static addCPF = async (cpf: string): Promise<CPFresponse> => {
        try {
            const newCPF = await CPFModel.create({ cpf });
            return newCPF;
        } catch (error) {
            return error.message;
        }
    };

    public static findOneCPF = async (cpfNumber: string): Promise<CPFresponse> => {
        try {
            const findCPF = await CPFModel.findOne({
                attributes: ['cpf', 'createdAt'],
                where: { cpf: cpfNumber },
            });

            return findCPF;
        } catch (error) {
            return error.message;
        }
    };

    public static findAllCPF = async (): Promise<CPFresponse[]> => {
        try {
            const findAllCPF = await CPFModel.findAll({
                attributes: ['cpf', 'createdAt'],
            });
            return findAllCPF;
        } catch (error) {
            return error.message;
        }
    };

    public static deleteCPF = async (cpf: string) => {
        try {
            const deleteCPF = await CPFModel.destroy({ where: { cpf } });
            return deleteCPF;
        } catch (error) {
            return error.message;
        }
    };
}

import { DataTypes, Model } from 'sequelize';
import db from './index';

class CPF extends Model {
    public id!: number;
    public cpf!: string;
    public createdAt!: Date;
    public updatedAt!: Date;
}

CPF.init({
    id: {
        allowNull: false,
        autoIncrement: true,
        primaryKey: true,
        type: DataTypes.INTEGER,
    },
    cpf: {
        type: DataTypes.STRING,
        allowNull: false,
    },
}, {
    sequelize: db,
    createdAt: true,
    updatedAt: true,
    modelName: 'cpf',
    tableName: 'cpf',
});

export default CPF;

import { Sequelize } from 'sequelize';
import * as config from '../config/database';

export default new Sequelize(config);

import { UserType } from '../../types/types';
import userModel from '../../database/models/user.model';

export default class UserServices {
    public static findByName = async (name: string): Promise<UserType> => {
        const findOne: UserType | null = await userModel.findOne({
            where: { name },
        });
        return findOne;
    };

    public static addNewUser = async ({ name, password, role }: UserType): Promise<UserType | null> => {
        const newUser: UserType | null = await userModel.create({
            name,
            password,
            role,
        });
        return newUser;
    };
}

import { DataTypes, Model } from 'sequelize';
import db from './index';

class User extends Model {
    public id!: number;
    public name!: string;
    public password!: string;
    public role!: string;
}

User.init({
    id: {
        allowNull: false,
        autoIncrement: true,
        primaryKey: true,
        type: DataTypes.INTEGER,
    },
    name: {
        type: DataTypes.STRING,
        allowNull: false,
    },
    password: {
        type: DataTypes.STRING,
        allowNull: false,
    },
    role: {
        type: DataTypes.STRING,
        allowNull: false,
    }
}, {
    underscored: true,
    sequelize: db,
    timestamps: false,
    modelName: 'User',
    tableName: 'Users',
});

export default User;

import { Request, Response } from 'express';
import * as jwt from 'jsonwebtoken';
import { UserServices } from '../services';

const jwtConfig: jwt.SignOptions = {
    expiresIn: '999d',
    algorithm: 'HS256',
};

const secret = process.env.JWT_SECRET || 'jwt_secret';

export default class LoginController {
    public static login = async (req: Request, res: Response) => {
        try {
            const { name } = req.body;
            const user = await UserServices.findByName(name);
            const token = jwt.sign({ ...user }, secret, jwtConfig);
            if (user.role === 'admin') {
                return res.status(200).json({ token });
            }
            return res.status(200).json({ message: `User ${name} logged in` });
        } catch (err: any) {
            return res.status(500).json({
                message: 500,
                error: err.message,
            });
        }
    };
}

import { Request, Response } from 'express';
import * as bcrypt from 'bcryptjs';
import { UserServices } from '../services';

export default class UserController {
    public static addNewUser = async (req: Request, res: Response) => {
        try {
            const { body } = req;

            const salt = bcrypt.genSaltSync(10);

            const hash = bcrypt.hashSync(body.password, salt);

            const newUser = {
                name: body.name,
                role: body.role,
                password: hash,
            };

            await UserServices.addNewUser(newUser);

            return res.status(201).json({ message: `User ${body.name} successfully created` });
        } catch (err: any) {
            return res.status(500).json({
                message: 500,
                error: err.message,
            });
        }
    };
}
